const initialState = {
  products: [{
    id: 0,
    title: 'T-shirt',
    description: 'black',
    price: 0.99
  }]
};

export default (state = initialState, action) => {
  switch(action.type) {
    case 'SET_PRODUCTS':
      return {
        products: action.payload
      };
    case 'ADD_PRODUCTS':
      return {
        ...state,
        products: [
          ...state.products,
          action.payload
        ]
      };
    default:
      return state;
  }
}
